package flow_Controls;

public class IfElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number=-13;    
	    if(number>0){  
	    System.out.println("POSITIVE");  
	    }else if(number==0){  
	    System.out.println("NEGATIVE");  
	    }else{  
	    System.out.println("ZERO");  
	   }  
	}    
	}    


